package com.example.moneytwork.core.di

import android.app.Application
import androidx.room.Room
import com.example.moneytwork.core.constants.Constants
import com.example.moneytwork.data.local.database.MoneytworkDatabase
import com.example.moneytwork.data.remote.api.CoinGeckoApi
import com.example.moneytwork.data.repository.CryptoRepositoryImpl
import com.example.moneytwork.domain.repository.CryptoRepository
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.components.SingletonComponent
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import java.util.concurrent.TimeUnit
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object AppModule {

    @Provides
    @Singleton
    fun provideOkHttpClient(): OkHttpClient {
        val loggingInterceptor = HttpLoggingInterceptor().apply {
            level = HttpLoggingInterceptor.Level.BODY
        }

        return OkHttpClient.Builder()
            .addInterceptor(loggingInterceptor)
            .connectTimeout(30, TimeUnit.SECONDS)
            .readTimeout(30, TimeUnit.SECONDS)
            .writeTimeout(30, TimeUnit.SECONDS)
            .build()
    }

    @Provides
    @Singleton
    fun provideCoinGeckoApi(okHttpClient: OkHttpClient): CoinGeckoApi {
        return Retrofit.Builder()
            .baseUrl(Constants.COINGECKO_BASE_URL)
            .client(okHttpClient)
            .addConverterFactory(GsonConverterFactory.create())
            .build()
            .create(CoinGeckoApi::class.java)
    }

    @Provides
    @Singleton
    fun provideMoneytworkDatabase(app: Application): MoneytworkDatabase {
        return Room.databaseBuilder(
            app,
            MoneytworkDatabase::class.java,
            Constants.DATABASE_NAME
        ).build()
    }

    @Provides
    @Singleton
    fun provideCryptoRepository(
        api: CoinGeckoApi,
        db: MoneytworkDatabase
    ): CryptoRepository {
        return CryptoRepositoryImpl(api, db.coinDao, db.watchlistDao)
    }
}

package com.example.moneytwork.core.di

import android.app.Application
import androidx.room.Room
import com.example.moneytwork.core.constants.Constants
import com.example.moneytwork.data.local.database.MoneytworkDatabase
import com.example.moneytwork.data.remote.api.CoinGeckoApi
import com.example.moneytwork.data.repository.CryptoRepositoryImpl
import com.example.moneytwork.domain.repository.CryptoRepository
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.components.SingletonComponent
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import java.util.concurrent.TimeUnit
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object AppModule {

    @Provides
    @Singleton
    fun provideOkHttpClient(): OkHttpClient {
        val loggingInterceptor = HttpLoggingInterceptor().apply {
            level = HttpLoggingInterceptor.Level.BODY
        }
        
        return OkHttpClient.Builder()
            .addInterceptor(loggingInterceptor)
            .connectTimeout(30, TimeUnit.SECONDS)
            .readTimeout(30, TimeUnit.SECONDS)
            .writeTimeout(30, TimeUnit.SECONDS)
            .build()
    }

    @Provides
    @Singleton
    fun provideCoinGeckoApi(okHttpClient: OkHttpClient): CoinGeckoApi {
        return Retrofit.Builder()
            .baseUrl(Constants.COINGECKO_BASE_URL)
            .client(okHttpClient)
            .addConverterFactory(GsonConverterFactory.create())
            .build()
            .create(CoinGeckoApi::class.java)
    }

    @Provides
    @Singleton
    fun provideFinnhubApi(okHttpClient: OkHttpClient): FinnhubApi {
        return Retrofit.Builder()
            .baseUrl(Constants.FINNHUB_BASE_URL)
            .client(okHttpClient)
            .addConverterFactory(GsonConverterFactory.create())
            .build()
            .create(FinnhubApi::class.java)
    }

    @Provides
    @Singleton
    fun provideMoneytworkDatabase(app: Application): MoneytworkDatabase {
        return Room.databaseBuilder(
            app,
            MoneytworkDatabase::class.java,
            Constants.DATABASE_NAME
        ).build()
    }

    @Provides
    @Singleton
    fun provideCryptoRepository(
        api: CoinGeckoApi,
        db: MoneytworkDatabase
    ): CryptoRepository {
        return CryptoRepositoryImpl(api, db.coinDao, db.watchlistDao)
    }
}

package com.example.moneytwork.data.remote.api

import com.example.moneytwork.data.remote.dto.StockProfileDto
import com.example.moneytwork.data.remote.dto.StockQuoteDto
import com.example.moneytwork.data.remote.dto.StockSymbolDto
import retrofit2.http.GET
import retrofit2.http.Query

interface FinnhubApi {

    @GET("quote")
    suspend fun getStockQuote(
        @Query("symbol") symbol: String,
        @Query("token") apiKey: String = "YOUR_API_KEY_HERE" // You'll need to get a free API key from finnhub.io
    ): StockQuoteDto

    @GET("stock/profile2")
    suspend fun getStockProfile(
        @Query("symbol") symbol: String,
        @Query("token") apiKey: String = "YOUR_API_KEY_HERE"
    ): StockProfileDto

    @GET("stock/symbol")
    suspend fun getStockSymbols(
        @Query("exchange") exchange: String = "US",
        @Query("token") apiKey: String = "YOUR_API_KEY_HERE"
    ): List<StockSymbolDto>
}

package com.example.moneytwork.data.remote.dto

import com.google.gson.annotations.SerializedName

data class StockQuoteDto(
    @SerializedName("c") val currentPrice: Double,
    @SerializedName("h") val highPrice: Double,
    @SerializedName("l") val lowPrice: Double,
    @SerializedName("o") val openPrice: Double,
    @SerializedName("pc") val previousClose: Double,
    @SerializedName("t") val timestamp: Long
)

data class StockSymbolDto(
    @SerializedName("description") val description: String,
    @SerializedName("displaySymbol") val displaySymbol: String,
    @SerializedName("symbol") val symbol: String,
    @SerializedName("type") val type: String
)

data class StockProfileDto(
    @SerializedName("country") val country: String?,
    @SerializedName("currency") val currency: String?,
    @SerializedName("exchange") val exchange: String?,
    @SerializedName("finnhubIndustry") val industry: String?,
    @SerializedName("logo") val logo: String?,
    @SerializedName("marketCapitalization") val marketCap: Double?,
    @SerializedName("name") val name: String,
    @SerializedName("phone") val phone: String?,
    @SerializedName("shareOutstanding") val sharesOutstanding: Double?,
    @SerializedName("ticker") val ticker: String,
    @SerializedName("weburl") val website: String?
)

package com.example.moneytwork.presentation.market.detail.components

import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.runtime.remember
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import com.example.moneytwork.domain.model.ChartData
import com.example.moneytwork.presentation.components.GlassCard
import com.example.moneytwork.presentation.market.detail.ChartTimeframe
import com.patrykandpatrick.vico.compose.axis.vertical.rememberStartAxis
import com.patrykandpatrick.vico.compose.chart.Chart
import com.patrykandpatrick.vico.compose.chart.line.lineChart
import com.patrykandpatrick.vico.compose.component.shape.shader.fromBrush
import com.patrykandpatrick.vico.compose.style.ProvideChartStyle
import com.patrykandpatrick.vico.core.chart.line.LineChart
import com.patrykandpatrick.vico.core.component.shape.shader.DynamicShaders
import com.patrykandpatrick.vico.core.entry.entryModelOf
import com.patrykandpatrick.vico.core.entry.FloatEntry
import androidx.compose.ui.graphics.Brush

@Composable
fun PriceChart(
    chartData: ChartData?,
    selectedTimeframe: String,
    onTimeframeSelected: (String) -> Unit,
    modifier: Modifier = Modifier
) {
    val timeframes = ChartTimeframe.entries

    GlassCard(
        modifier = modifier
            .fillMaxWidth()
            .padding(16.dp)
    ) {
        Text(
            text = "Price Chart",
            style = MaterialTheme.typography.titleLarge,
            fontWeight = FontWeight.Bold,
            color = Color.White
        )

        Spacer(modifier = Modifier.height(16.dp))

        // Timeframe selector with pipes
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.Center,
            verticalAlignment = androidx.compose.ui.Alignment.CenterVertically
        ) {
            timeframes.forEachIndexed { index, timeframe ->
                Text(
                    text = timeframe.label,
                    modifier = Modifier.clickable { onTimeframeSelected(timeframe.days) },
                    style = MaterialTheme.typography.labelLarge,
                    color = if (selectedTimeframe == timeframe.days)
                        MaterialTheme.colorScheme.primary
                    else
                        Color.White.copy(alpha = 0.6f),
                    fontWeight = if (selectedTimeframe == timeframe.days)
                        FontWeight.Bold
                    else
                        FontWeight.Normal
                )
                if (index < timeframes.size - 1) {
                    Text(
                        text = " | ",
                        color = Color.White.copy(alpha = 0.3f),
                        modifier = Modifier.padding(horizontal = 4.dp)
                    )
                }
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // Chart
        if (chartData != null && chartData.prices.isNotEmpty()) {
            val chartEntries = remember(chartData) {
                chartData.prices.mapIndexed { index, point ->
                    FloatEntry(index.toFloat(), point.price.toFloat())
                }
            }

            val chartEntryModel = remember(chartEntries) {
                entryModelOf(chartEntries)
            }

            ProvideChartStyle {
                Chart(
                    chart = lineChart(
                        lines = listOf(
                            LineChart.LineSpec(
                                lineColor = Color(0xFF2962FF).hashCode(),
                                lineBackgroundShader = DynamicShaders.fromBrush(
                                    Brush.verticalGradient(
                                        listOf(
                                            Color(0xFF2962FF).copy(alpha = 0.5f),
                                            Color.Transparent
                                        )
                                    )
                                )
                            )
                        )
                    ),
                    model = chartEntryModel,
                    startAxis = rememberStartAxis(),
                    bottomAxis = null,
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(250.dp)
                )
            }

            Spacer(modifier = Modifier.height(16.dp))

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween
            ) {
                Column {
                    Text(
                        text = "Low",
                        style = MaterialTheme.typography.labelMedium,
                        color = Color.White.copy(alpha = 0.6f)
                    )
                    Text(
                        text = "$${String.format("%.2f", chartData.prices.minOf { it.price })}",
                        style = MaterialTheme.typography.bodyLarge,
                        color = Color.White,
                        fontWeight = FontWeight.SemiBold
                    )
                }
                Column(horizontalAlignment = androidx.compose.ui.Alignment.End) {
                    Text(
                        text = "High",
                        style = MaterialTheme.typography.labelMedium,
                        color = Color.White.copy(alpha = 0.6f)
                    )
                    Text(
                        text = "$${String.format("%.2f", chartData.prices.maxOf { it.price })}",
                        style = MaterialTheme.typography.bodyLarge,
                        color = Color.White,
                        fontWeight = FontWeight.SemiBold
                    )
                }
            }
        } else {
            Box(
                modifier = Modifier
                    .fillMaxWidth()
                    .height(250.dp),
                contentAlignment = androidx.compose.ui.Alignment.Center
            ) {
                CircularProgressIndicator()
            }
        }
    }
}

