/** * Unit tests for Transaction data model * Tests that our transaction entity works correctly */ class TransactionEntityTest {
@Test fun `transaction entity creates correctly with all fields`() { // Test creating a BUY transaction val transaction = TransactionEntity( id = 1, assetId = "bitcoin", assetType = "CRYPTO", assetName = "Bitcoin", assetSymbol = "BTC", transactionType = "BUY", amount = 0.5, pricePerUnit = 50000.0, totalValue = 25000.0, timestamp = System.currentTimeMillis() )
assertEquals("bitcoin", transaction.assetId) assertEquals("CRYPTO", transaction.assetType) assertEquals("BTC", transaction.assetSymbol) assertEquals("BUY", transaction.transactionType) assertEquals(0.5, transaction.amount, 0.001) assertEquals(50000.0, transaction.pricePerUnit, 0.01) assertEquals(25000.0, transaction.totalValue, 0.01) }
@Test fun `transaction entity supports SELL operations`() { // Test creating a SELL transaction val transaction = TransactionEntity( id = 2, assetId = "ethereum", assetType = "CRYPTO", assetName = "Ethereum", assetSymbol = "ETH", transactionType = "SELL", amount = 1.0, pricePerUnit = 3000.0, totalValue = 3000.0, timestamp = System.currentTimeMillis() )
assertEquals("SELL", transaction.transactionType) assertEquals("ethereum", transaction.assetId) assertEquals(1.0, transaction.amount, 0.001) }
@Test fun `transaction entity supports stock transactions`() { // Test stock transaction val transaction = TransactionEntity( id = 3, assetId = "AAPL", assetType = "STOCK", assetName = "Apple Inc.", assetSymbol = "AAPL", transactionType = "BUY", amount = 10.0, pricePerUnit = 150.0, totalValue = 1500.0, timestamp = System.currentTimeMillis() )
assertEquals("STOCK", transaction.assetType) assertEquals("AAPL", transaction.assetSymbol) }
@Test fun `transaction timestamps are stored correctly`() { val currentTime = System.currentTimeMillis() val transaction = TransactionEntity( id = 1, assetId = "bitcoin", assetType = "CRYPTO", assetName = "Bitcoin", assetSymbol = "BTC", transactionType = "BUY", amount = 0.1, pricePerUnit = 45000.0, totalValue = 4500.0, timestamp = currentTime )
assertEquals(currentTime, transaction.timestamp) assertTrue("Timestamp should be positive", transaction.timestamp > 0) } }

package com.example.moneytwork.domain.model

import org.junit.Assert.assertEquals
import org.junit.Assert.assertTrue
import org.junit.Test

class AssetCalculationTest {

    @Test
    fun `price change percentage should format correctly`() {
        // Given
        val positiveChange = 5.67
        val negativeChange = -3.42

        // When
        val formattedPositive = String.format("%.2f%%", positiveChange)
        val formattedNegative = String.format("%.2f%%", negativeChange)

        // Then
        assertEquals("5.67%", formattedPositive)
        assertEquals("-3.42%", formattedNegative)
    }

    @Test
    fun `portfolio value calculation should be accurate`() {
        // Given
        val quantity = 0.5
        val pricePerUnit = 50000.0
        val invested = 20000.0

        // When
        val currentValue = quantity * pricePerUnit
        val profit = currentValue - invested
        val profitPercentage = (profit / invested) * 100

        // Then
        assertEquals(25000.0, currentValue, 0.01)
        assertEquals(5000.0, profit, 0.01)
        assertEquals(25.0, profitPercentage, 0.01)
    }

    @Test
    fun `price formatting should handle large numbers`() {
        // Given
        val price = 1234567.89

        // When
        val formatted = String.format("$%,.2f", price)

        // Then
        assertTrue(formatted.contains("1,234,567.89") || formatted.contains("1234567.89"))
    }

    @Test
    fun `percentage change should indicate trend direction`() {
        // Given
        val positiveChange = 5.0
        val negativeChange = -3.0
        val noChange = 0.0

        // Then
        assertTrue(positiveChange > 0)  // Uptrend
        assertTrue(negativeChange < 0)  // Downtrend
        assertEquals(0.0, noChange, 0.0)  // No change
    }

    @Test
    fun `calculate market cap from price and supply`() {
        // Given
        val price = 50000.0
        val circulatingSupply = 19000000.0

        // When
        val marketCap = price * circulatingSupply

        // Then
        assertEquals(950000000000.0, marketCap, 0.01)  // 950 billion
    }

    @Test
    fun `calculate 24h high and low range`() {
        // Given
        val high24h = 52000.0
        val low24h = 48000.0

        // When
        val range = high24h - low24h
        val midpoint = (high24h + low24h) / 2

        // Then
        assertEquals(4000.0, range, 0.01)
        assertEquals(50000.0, midpoint, 0.01)
    }
}

package com.example.moneytwork.domain.model

import org.junit.Assert.*
import org.junit.Test

/**
 * Unit tests for domain models
 * Tests our Transaction and AssetType enums work correctly
 */
class DomainModelTest {

    @Test
    fun `TransactionType enum has BUY and SELL`() {
        // Test that our TransactionType enum is defined correctly
        assertEquals(TransactionType.BUY, TransactionType.valueOf("BUY"))
        assertEquals(TransactionType.SELL, TransactionType.valueOf("SELL"))
    }

    @Test
    fun `AssetType enum has CRYPTO and STOCK`() {
        // Test that our AssetType enum is defined correctly
        assertEquals(AssetType.CRYPTO, AssetType.valueOf("CRYPTO"))
        assertEquals(AssetType.STOCK, AssetType.valueOf("STOCK"))
    }

    @Test
    fun `Transaction model creates with correct types`() {
        // Test creating a Transaction with enums
        val transaction = Transaction(
            id = 1,
            assetId = "bitcoin",
            assetType = AssetType.CRYPTO,
            assetName = "Bitcoin",
            assetSymbol = "BTC",
            transactionType = TransactionType.BUY,
            amount = 0.5,
            pricePerUnit = 50000.0,
            totalValue = 25000.0,
            timestamp = System.currentTimeMillis(),
            notes = "Test transaction"
        )

        assertEquals(AssetType.CRYPTO, transaction.assetType)
        assertEquals(TransactionType.BUY, transaction.transactionType)
        assertEquals("bitcoin", transaction.assetId)
        assertEquals(0.5, transaction.amount, 0.001)
    }

    @Test
    fun `Transaction supports both BUY and SELL operations`() {
        // Test BUY transaction
        val buyTransaction = Transaction(
            id = 1,
            assetId = "bitcoin",
            assetType = AssetType.CRYPTO,
            assetName = "Bitcoin",
            assetSymbol = "BTC",
            transactionType = TransactionType.BUY,
            amount = 0.1,
            pricePerUnit = 50000.0,
            totalValue = 5000.0,
            timestamp = System.currentTimeMillis()
        )

        assertEquals(TransactionType.BUY, buyTransaction.transactionType)

        // Test SELL transaction
        val sellTransaction = buyTransaction.copy(
            id = 2,
            transactionType = TransactionType.SELL
        )

        assertEquals(TransactionType.SELL, sellTransaction.transactionType)
    }

    @Test
    fun `AssetHolding model stores portfolio data correctly`() {
        // Test that AssetHolding can store complete portfolio information
        val holding = AssetHolding(
            assetId = "bitcoin",
            assetType = AssetType.CRYPTO,
            assetName = "Bitcoin",
            assetSymbol = "BTC",
            totalAmount = 0.5,
            averageBuyPrice = 45000.0,
            totalInvested = 22500.0,
            currentPrice = 50000.0,
            currentValue = 25000.0,
            profitLoss = 2500.0,
            profitLossPercentage = 11.11,
            transactions = emptyList()
        )

        assertEquals("bitcoin", holding.assetId)
        assertEquals(AssetType.CRYPTO, holding.assetType)
        assertEquals(0.5, holding.totalAmount, 0.001)
        assertEquals(45000.0, holding.averageBuyPrice, 0.01)
        assertTrue("Profit should be positive", holding.profitLoss > 0)
    }
}

package com.example.moneytwork.presentation.portfolio

import org.junit.Assert.assertEquals
import org.junit.Test

class PortfolioCalculationTest {

    @Test
    fun `calculate portfolio value with single crypto`() {
        // Given: User owns 0.5 BTC bought at $40k, current price is $50k
        val amount = 0.5
        val buyPrice = 40000.0
        val currentPrice = 50000.0

        // When
        val invested = amount * buyPrice // 20000
        val currentValue = amount * currentPrice // 25000
        val profit = currentValue - invested // 5000
        val profitPercent = (profit / invested) * 100 // 25%

        // Then
        assertEquals(20000.0, invested, 0.01)
        assertEquals(25000.0, currentValue, 0.01)
        assertEquals(5000.0, profit, 0.01)
        assertEquals(25.0, profitPercent, 0.01)
    }

    @Test
    fun `calculate portfolio value with multiple purchases same asset`() {
        // Given: Bought BTC at different prices
        // Purchase 1: 0.01 BTC at $40k = $400
        // Purchase 2: 0.01 BTC at $50k = $500
        // Current price: $60k
        val totalAmount = 0.02
        val totalInvested = 900.0 // 400 + 500
        val currentPrice = 60000.0

        // When
        val currentValue = totalAmount * currentPrice // 1200
        val profit = currentValue - totalInvested // 300
        val profitPercent = (profit / totalInvested) * 100 // 33.33%

        // Then
        assertEquals(1200.0, currentValue, 0.01)
        assertEquals(300.0, profit, 0.01)
        assertEquals(33.33, profitPercent, 0.1)
    }

    @Test
    fun `calculate average buy price`() {
        // Given: Multiple purchases
        val purchase1Amount = 0.01
        val purchase1Price = 40000.0
        val purchase2Amount = 0.01
        val purchase2Price = 50000.0

        // When
        val totalAmount = purchase1Amount + purchase2Amount // 0.02
        val totalValue = (purchase1Amount * purchase1Price) + (purchase2Amount * purchase2Price) // 900
        val averagePrice = totalValue / totalAmount // 45000

        // Then
        assertEquals(0.02, totalAmount, 0.001)
        assertEquals(900.0, totalValue, 0.01)
        assertEquals(45000.0, averagePrice, 0.01)
    }

    @Test
    fun `calculate portfolio loss scenario`() {
        // Given: Bought at high, price dropped
        val amount = 1.0
        val buyPrice = 50000.0
        val currentPrice = 40000.0

        // When
        val invested = amount * buyPrice // 50000
        val currentValue = amount * currentPrice // 40000
        val loss = currentValue - invested // -10000
        val lossPercent = (loss / invested) * 100 // -20%

        // Then
        assertEquals(50000.0, invested, 0.01)
        assertEquals(40000.0, currentValue, 0.01)
        assertEquals(-10000.0, loss, 0.01)
        assertEquals(-20.0, lossPercent, 0.01)
    }

    @Test
    fun `calculate total portfolio value multiple assets`() {
        // Given: Portfolio with BTC and ETH
        // BTC: 0.5 at $50k = $25k
        // ETH: 5.0 at $3k = $15k
        val btcValue = 0.5 * 50000.0
        val ethValue = 5.0 * 3000.0

        // When
        val totalPortfolioValue = btcValue + ethValue

        // Then
        assertEquals(25000.0, btcValue, 0.01)
        assertEquals(15000.0, ethValue, 0.01)
        assertEquals(40000.0, totalPortfolioValue, 0.01)
    }
}

package com.example.moneytwork.presentation.portfolio

import org.junit.Assert.*
import org.junit.Test

/**
 * Unit tests for Portfolio State
 * Tests the PortfolioState data class
 */
class PortfolioStateTest {

    @Test
    fun `PortfolioState initializes with default values`() {
        // Test that PortfolioState can be created with defaults
        val state = PortfolioState()

        assertNotNull("State should not be null", state)
        assertFalse("Initial loading should be false", state.isLoading)
        assertEquals("Initial total investment should be 0", 0.0, state.totalInvestment, 0.01)
        assertEquals("Initial current value should be 0", 0.0, state.currentValue, 0.01)
        assertEquals("Initial profit/loss should be 0", 0.0, state.profitLoss, 0.01)
    }

    @Test
    fun `PortfolioState stores profit correctly`() {
        // Test that state object holds profit values correctly
        val state = PortfolioState(
            isLoading = false,
            totalInvestment = 10000.0,
            currentValue = 12000.0,
            profitLoss = 2000.0,
            profitLossPercentage = 20.0
        )

        assertEquals(10000.0, state.totalInvestment, 0.01)
        assertEquals(12000.0, state.currentValue, 0.01)
        assertEquals(2000.0, state.profitLoss, 0.01)
        assertEquals(20.0, state.profitLossPercentage, 0.01)
        assertFalse(state.isLoading)
    }

    @Test
    fun `PortfolioState stores loss correctly`() {
        // Test loss scenario
        val state = PortfolioState(
            isLoading = false,
            totalInvestment = 10000.0,
            currentValue = 8000.0,
            profitLoss = -2000.0,
            profitLossPercentage = -20.0
        )

        assertTrue("Profit/loss should be negative", state.profitLoss < 0)
        assertTrue("Percentage should be negative", state.profitLossPercentage < 0)
        assertEquals(-2000.0, state.profitLoss, 0.01)
    }
}
